--paging SELECT A.*, B.* FROM( SELECT TT1.rnum as num, TT1.charger_no, TT1.connector, TT1.image, TT1.ev_current, TT1.ev_voltage, TT1.ev_power, TT1.ev_level, TT1.ev_support FROM( SELECT ROWNUM AS rnum, T1.* FROM( SELECT * FROM charger --WHERE(10:충전기명,20:충전전류,30:충전전력,40:지원차량)
ORDER BY charger_no ASC )T1 WHERE ROWNUM <= #{pageSize} * (#{pageNo} -1) + #{pageSize} )TT1 WHERE rnum >= #{pageSize} * (#{pageNo} -1) + 1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM charger --WHERE(10:충전기명,20:충전전류,30:충전전력,40:지원차량)
)B
