--paging SELECT A.*, B.* FROM( SELECT TT1.rnum as num, TT1.provider_seq, TT1.image, TT1.enterprenuer, TT1.rapid_below100, TT1.rapid_above100, TT1.slow_fee FROM( SELECT ROWNUM AS rnum, T1.* FROM( SELECT * FROM charging_fee --WHERE(10:사업자명)
ORDER BY provider_seq ASC )T1 WHERE ROWNUM <= #{pageSize} * (#{pageNo} -1) + #{pageSize} )TT1 WHERE rnum >= #{pageSize} * (#{pageNo} -1) + 1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM charging_fee --WHERE(10:자동차명)
)B
