ORDER BY standard_date ASC )T1 WHERE ROWNUM <= #{pageSize} * (#{pageNo} -1) + #{pageSize} )TT1 WHERE rnum >= #{pageSize} * (#{pageNo} -1) + 1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM regional_count --WHERE(10:기준일)
)B
