mapper.select:
SELECT A.*,B.* FROM ( SELECT t2.rnum as num, t2.member_seq as mSeq, t2.id as userId, t2.nickname as nickname, t2.password as userPw, t2.name as name, TO_CHAR(t2.birth,'YYYYMMDD') as birth, t2.email as email, t2.backup_question as backupQuestion, t2.backup_answer as backupAnswer, t2.point as point, TO_CHAR(t2.join_date,'YYYYMMDD') as joinDate, t2.status as status FROM ( SELECT ROWNUM as RNUM, T1.* FROM ( SELECT * FROM member
ORDER BY join_date DESC )T1 WHERE ROWNUM <= #{pageSize} * #{pageNo} )T2 WHERE RNUM >= #{pageSize} * (#{pageNo} - 1) + 1 )A CROSS JOIN ( SELECT COUNT(*) as totalCnt FROM member
)B

SELECT COUNT(*)
FROM member
WHERE id = #{userId}

SELECT COUNT(*)
FROM member
WHERE nickname = #{nickname}

SELECT member_seq
FROM member
WHERE id = #{userId}
AND password = #{userPw}

SELECT id
FROM member
WHERE email = #{email}

SELECT id
FROM member
WHERE name = #{name}
AND birth = #{birth}

SELECT password
FROM member
WHERE id = #{userId}
AND backup_question = #{backupQuestion}
AND backup_answer = #{backupAnswer}

mapper.update:
UPDATE member
SET nickname = #{nickname}
WHERE member_seq = #{mSeq}

UPDATE member
SET password = #{userPw}
WHERE member_seq = #{mSeq}

UPDATE member
SET email = #{email}
WHERE member_seq = #{mSeq}
